mapper.insert:

	select survey_seq.nextval from dual

INSERT INTO survey (
	srv_no
	, mem_no
	, srv_name
	, srv_category
	, srv_end_date
	, srv_reg_date
) VALUES (
	#{srvNo}
	, #{memNo}
	, #{srvName}
	, #{srvCategory}
	, TO_DATE(#{srvEndDate}, 'YYYY-MM-DD HH24:MI:SS')
	, sysDate
)


	select survey_question_seq.nextval from dual

INSERT INTO survey_question (
	srv_qst_no
	, srv_no
	, qst_no
	, srv_qst
) VALUES (
	#{srvQstNo}
	, #{srvNo}
	, #{qstNo}
	, #{srvQst}
)

INSERT INTO survey_question_choice (
	qst_ch_no
	, srv_qst_no
	, srv_no
	, ch_no
	, ch_str
) VALUES (
	survey_question_choice_seq.nextval
	, #{srvQstNo}
	, #{srvNo}
	, #{chNo}
	, #{chStr}
)
